mapper.select:
SELECT class_code, class_name, category, class_grade, class_credit, class_status
FROM tb_class
WHERE


        class_code = #{value}


        class_name LIKE CONCAT('%', #{value}, '%')


        class_status = #{value}


        category LIKE CONCAT('%', #{value}, '%')


        class_credit = #{value}


        class_grade = #{value}


ORDER BY class_code
